import { useEffect } from 'react';
import { useNavigate } from 'react-router';
import Spline from '@splinetool/react-spline';
import './Home.css'; // Make sure to import the CSS file

const Home = () => {
    const nav = useNavigate();

    useEffect(() => {
        if (localStorage.getItem("userInfo1")) {
            nav('/s');
        }
    }, [nav]);

    return (
        <div className="homepage">
            {/* Spline background */}
            <Spline scene="https://prod.spline.design/bBt1Gc0FcFxkJIeA/scene.splinecode" className="spline-background" />

            {/* Content */}
            <div className="jumbotron">
                <div className="container">
                    <h1>SocietySpot</h1>
                    <a className="btn btn-light btn-lg" href="/register" role="button">
                        Register
                    </a>
                    <a className="btn btn-dark btn-lg" href="/login" role="button">
                        Login
                    </a>
                </div>
            </div>
        </div>
    );
};

export default Home;
