import { useEffect } from 'react';
import { useNavigate } from 'react-router';import './Home.css'; // Optional: for component-specific styling

const Home = () => {
    const nav = useNavigate();

    // Handle navigation based on localStorage
    useEffect(() => {
        if (localStorage.getItem("userInfo1")) {
            nav('/s');
        }
    }, [nav]);

    // Dynamically load the Spline script
    useEffect(() => {
        const script = document.createElement('script');
        script.type = 'module';
        script.src = 'https://unpkg.com/@splinetool/viewer@1.9.46/build/spline-viewer.js';
        document.body.appendChild(script);

        return () => {
            // Cleanup the script on component unmount
            document.body.removeChild(script);
        };
    }, []);

    return (
        <div>
            <div className="jumbotron centered align-items-center">
                <div className="container">
                    <h1 className="display-3 mt-3 mb-3">SocietySpot</h1>
                    <spline-viewer
                        url="https://prod.spline.design/bBt1Gc0FcFxkJIeA/scene.splinecode"
                        className="spline-background"
                    ></spline-viewer>
                    <a className="btn btn-light btn-lg mr-2" href="/register" role="button">
                        Register
                    </a>
                    <a className="btn btn-dark btn-lg" href="/login" role="button">
                        Login
                    </a>
                </div>
            </div>
        </div>
    );
};

export default Home;
